import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    
    constructor(){
        super();
        console.log("hello i am cons from news compo");
        this.state={
            articles:[],
            loading:true
        }
    }
   async componentDidMount() {  // life cycle method  //used to update all data&articles
        console.log("cdm");
        let url="https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=0bd8aba923c54ada91b975e0994cdef4"
        let data= await fetch(url);
        let parsedata=await data.json()
        console.log(parsedata);
        this.setState({articles:parsedata.articles})
    }
    handlepreviousclick=()=>{
       
    }
    handleNextclick=()=>{
           this.setState({
            page: this.state.page+1
           })
    }
  render() {
    return (
      <div className='container my-3'>
        
        <h1>NEWSAPP-Top Headlines</h1>
        <div className="row">
            {this.state.articles.map((element)=>{

            
       return <div className="col-md-4" key={element.url}>

        <NewsItem  title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} imgurl={element.urlToImage} newsurl={element.url}/>
        
        </div>
       })}
      
        </div>
        <div className="container d-flex justify-content-between">
            <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePreviousclick}> &larr; Previous</button>
            <button type="buutton" class="btn btn-dark" onClick={this.handleNextclick}>Next &rarr;</button>
        </div>

        

      </div>
    )
  }
}
