import React, { Component } from 'react';
import NewsItem from './NewsItem';

export default class News extends Component {
  constructor() {
    super();
    console.log("Hello, I am cons from News component.");
    this.state = {
      articles: [],
      loading: true,
      page: 1, // Initialize the page state
      
}
 
    }
  
  async componentDidMount() {
    console.log("cdm");
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=0bd8aba923c54ada91b975e0994cdef4&page=1&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState({ articles: parsedata.articles ,totalarticles: parsedata.totalResults});
  }

  handlePreviousclick = async () => {
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=0bd8aba923c54ada91b975e0994cdef4&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState({
      page: this.state.page - 1,
      articles: parsedata.articles,
    });
  };

  handleNextclick = async () => {
    if(!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
    let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=0bd8aba923c54ada91b975e0994cdef4&page=${this.state.page + 1}&pagesize=${this.props.pageSize}`;
    this.setState({loading: true})
    let data = await fetch(url);
    let parsedata = await data.json();
    console.log(parsedata);
    this.setState({
      page: this.state.page + 1,
      articles: parsedata.articles,
    });
  };
}

  render() {
    return (
      <div className='container my-3'>
        <h1 className="text-center">NEWSAPP - Top Headlines</h1>
        {this.state.loading  && <spinner/>}
        <div className="row">
          {this.state.articles.map((element) => (
            <div className="col-md-4" key={element.url}>
              <NewsItem
                title={element.title ? element.title.slice(0, 45) : ""}
                description={element.description ? element.description.slice(0, 88) : ""}
                imgurl={element.urlToImage}
                newsurl={element.url}
              />
            </div>
          ))}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePreviousclick}
          >
            &larr; Previous
          </button>
          <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)}type="button" className="btn btn-dark" onClick={this.handleNextclick}>
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}
